$(function() {
	$(".weixin-logo-icon").mouseleave(function() {
		$(this).prev().find(".popover").removeClass("hover");
	}).mouseenter(function() {
		$(this).prev().find(".popover").addClass("hover");
	});
	$(".shouji-logo-icon").mouseleave(function() {
		$(this).prev().find(".popover").removeClass("hover");
	}).mouseenter(function() {
		$(this).prev().find(".popover").addClass("hover");
	});

});